#将data分割成更小的chunks
def get_data_chunks(data: str, chunk_size: int):
    # 使用正则表达式匹配所有的 triples
    pattern = r'<-(.*?)->'
    triples = re.findall(pattern, data, re.DOTALL)

    # 按第一个单词排序
    sorted_triples = sorted(triples, key=lambda x: x.split()[0] if x.strip() else '')

    # 将排序后的 triples 分块
    chunks = [sorted_triples[i:i + chunk_size] for i in range(0, len(sorted_triples), chunk_size)]

    return chunks

def get_data_max_chunk_size_chunks(data: str, max_chunk_size: int):
    # 使用正则表达式匹配所有的 triples
    pattern = r'<-(.*?)->'
    triples = re.findall(pattern, data, re.DOTALL)

    # 按第一个单词排序
    sorted_triples = sorted(triples, key=lambda x: x.split()[0] if x.strip() else '')

    # 将排序后的 triples 分块
    chunks = []
    current_chunk = []
    current_word = None

    for triple in sorted_triples:
        first_word = triple.split()[0] if triple.strip() else ''
        if first_word != current_word or len(current_chunk) >= max_chunk_size:
            if current_chunk:
                chunks.append(current_chunk)
            current_chunk = [triple]
            current_word = first_word
        else:
            current_chunk.append(triple)

    # 添加最后一个 chunk
    if current_chunk:
        chunks.append(current_chunk)
    return chunks


#创建knowledge hub 或者 database
def create_knowledge_hub(chunks: list):
    # 将每个内部列表转换为单个字符串
    processed_chunks = [" ".join(chunk) if isinstance(chunk, list) else chunk for chunk in chunks]

    model = SentenceTransformer('all-MiniLM-L6-v2')
    embeddings = model.encode(processed_chunks, show_progress_bar=True)

    index = faiss.IndexFlatL2(embeddings.shape[1])  # L2 distance for similarity
    index.add(np.array(embeddings).astype('float32'))  # Add embeddings to index

    return index


def retrieve_relevant_chunks(query, index, chunks, k=2):#bigger context
    model = SentenceTransformer('all-MiniLM-L6-v2')
    query_embedding = model.encode([query])[0]
    distances, indices = index.search(np.array([query_embedding]).astype('float32'), k)
    relevant_chunks = [chunks[i] for i in indices[0]]
    print("query from retrieve_relevant_chunks/n",query)
    print("index from retrieve_relevant_chunks/n",index)
    print("chunks from retrieve_relevant_chunks/n",chunks)
    print("relevant_chunks from retrieve_relevant_chunks/n",relevant_chunks)
    relevant_chunks = [' '.join(chunk) if isinstance(chunk, list) else chunk for chunk in relevant_chunks]
    return relevant_chunks
