def get_answer_LLM(question: str, query:str, data: str, chunk_size: int = 1000) -> str:
    if data == "":
        return ""
    # 创建文本块
    chunks = get_data_max_chunk_size_chunks(data, max_chunk_size=chunk_size)
    print("chunks")
    print(chunks)
    # 创建知识库
    knowledge_hub = create_knowledge_hub(chunks)
    # 检索相关块
    relevant_chunks = retrieve_relevant_chunks(query, knowledge_hub, chunks, k=2)
    # 将相关块组合成单一上下文
    context = ' '.join(relevant_chunks)
    # 初始化 generated_text
    generated_text = "没有生成的文本。"
    response = client.chat.completions.create(
        model="gpt-3.5-turbo-1106",
        messages=[
            {"role": "system", "content": "You are a helpful assistant."},
            {"role": "user", "content": context + " " + question}
        ]
    )
    if response.choices:
        first_choice = response.choices[0]

        generated_text = first_choice.message.content
        print("input context: ",context)
        print("model response: ",response)
        #role_from_result=first_choice.message.role
        #print("role_from_result:",role_from_result)
        # full_result = first_choice.message
        #print("full result:",full_result)
    return generated_text


def get_answers_LLM(questions: list[str], query: str, data: str, chunk_size: int = 1000) -> list[str]:
    # 检查数据是否为空
    if data == "":
        return [""] * len(questions)

    # 创建文本块
    chunks = get_data_max_chunk_size_chunks(data, max_chunk_size=chunk_size)

    # 创建知识库，仅一次
    knowledge_hub = create_knowledge_hub(chunks)

    # 初始化答案列表
    answers = []

    for question in questions:
        # 检索相关块
        relevant_chunks = retrieve_relevant_chunks(query, knowledge_hub, chunks, k=2)

        # 将相关块组合成单一上下文
        context = ' '.join(relevant_chunks)

        # 请求模型生成答案
        response = client.chat.completions.create(
            model="gpt-3.5-turbo-1106",
            messages=[
                {"role": "system", "content": "You are a helpful assistant."},
                {"role": "user", "content": context + " " + question}
            ]
        )

        if response.choices:
            first_choice = response.choices[0]
            generated_text = first_choice.message.content
        else:
            generated_text = "无法生成答案。"

        answers.append(generated_text)

        # 打印上下文和模型响应（可选）
        print("Input context: ", context)
        print("Model response: ", response)

    return answers

