def extract_sorted_entities_as_string(data):
    # 使用 || 分割实体
    entities = data.split('||')

    # 去除每个实体两端的空格，并删除空字符串
    entities = [entity.strip() for entity in entities if entity.strip()]

    # 去除重复的实体
    unique_entities = set(entities)

    # 对实体进行排序
    sorted_entities = sorted(unique_entities)

    # 将排序后的实体列表转换为字符串
    sorted_entities_string = ', '.join(sorted_entities)

    return sorted_entities_string

